mapper.update:
UPDATE location_board
SET overview = #{overview}
WHERE contentid = #{contentid}

mapper.select:
select count(1)
from location_board
where contentid = #{contentid}

select count(1)
from location_board
where contentid = #{contentid}
  AND contenttypeid = #{contenttypeid}

SELECT * FROM location_board where areacode = 1 AND sigungucode = 13
AND contenttypeid = #{contenttypeid}
AND title LIKE concat('%', #{criteria.keyword}, '%')
ORDER BY info_seq DESC LIMIT #{criteria.pageStart}, #{criteria.amount}

