mapper.update:
UPDATE review_board rb
    left outer join file f on rb.board_no = f.board_no
SET rb.board_title    = #{board_title},
    rb.board_contents = #{board_contents},
    f.file_name       = #{file_name}
WHERE rb.board_no = #{board_no}

UPDATE review_board
SET TNF = 'F'
WHERE board_no = #{board_no}
